package frontedfl

import (
	"crypto/x509"
	"io/ioutil"
	"os"
	"testing"

	"github.com/getlantern/fronted"
	"github.com/getlantern/keyman"
	"github.com/stretchr/testify/require"
)

var (
	cloudfrontID = "cloudfront"
	pingTestURL  = "http://d157vud77ygy87.cloudfront.net/ping"
	testHosts    = map[string]string(nil)
)

// ConfigureForTest sets global defaults appropriate for use in tests.
func ConfigureForTest(t *testing.T) {
	t.Helper()
	ConfigureHostAliasesForTest(t, testHosts)
}

// ConfigureHostAliasesForTest sets global defaults appropriate for use in tests. The hostAliases
// map is used to configure test providers (see fronted.Provider.HostAliases).
func ConfigureHostAliasesForTest(t *testing.T, hostAliases map[string]string) {
	t.Helper()

	tmpDir, err := ioutil.TempDir("", "frontedfl_ConfigureForTest_cachefolder")
	require.NoError(t, err, "failed to create cache folder for testing with frontedfl")
	Configure(testProviders(hostAliases), trustedCACerts(t), tmpDir)
	t.Cleanup(func() { os.RemoveAll(tmpDir) })
}

func trustedCACerts(t *testing.T) *x509.CertPool {
	t.Helper()
	certs := []string{}
	for _, ca := range testCAs {
		certs = append(certs, ca.certPEM)
	}
	pool, err := keyman.PoolContainingCerts(certs...)
	require.NoError(t, err, "failed to assemble certificate pool for testing with frontedfl")
	return pool
}

func testProviders(hostAliases map[string]string) map[string]*fronted.Provider {
	return map[string]*fronted.Provider{
		cloudfrontID: fronted.NewProvider(testHosts, pingTestURL, fronted.DefaultCloudfrontMasquerades, nil, nil),
	}
}

